import React, { useState } from 'react';
import SelectHoursPopUp from './selectHoursPopUp';
import Login from './login';
import Register from './register';
import ConfirmClosePopup from './confirmClosePopup';
import LoginPassword from './loginPassword';
import ConfirmPhoneNumber from './confirmPhoneNumber';
import ConfirmBooking from './confirmBooking';
import { CONFIRM_BOOKING, CONFIRM_PHONE_NUMBER, LOGIN, LOGIN_PASSWORD, REGISTER, SELECT_HOURS } from './stepsBooking';
import { BookingContext } from '../../../../context/bookingContext';

require('./styles.css')



const PopupBook = () => {

    const { stepPopupBook } = useContext(BookingContext);

    const [showConfirmClose, setShowConfirmClose] = useState(false);

    const [stepPopup, setStepPopup] = useState(1);

    const showStepPopup = () => {
        if (stepPopupBook == SELECT_HOURS) {
            return <SelectHoursPopUp setStepPopup={setStepPopup} />
        } else if (stepPopup == LOGIN) {
            return <Login setStepPopup={setStepPopup} />
        } else if (stepPopup == LOGIN_PASSWORD) {
            return <LoginPassword setStepPopup={setStepPopup} />
        } else if (stepPopup == CONFIRM_PHONE_NUMBER) {
            return <ConfirmPhoneNumber setStepPopup={setStepPopup} />
        } else if (stepPopup == REGISTER) {
            return <Register setStepPopup={setStepPopup} />
        } else if (stepPopup == CONFIRM_BOOKING) {
            return <ConfirmBooking setStepPopup={setStepPopup} />
        }

    }
    return (
        <>
            {showConfirmClose && (<div className='shadowCancelPopup'>
                <ConfirmClosePopup />
            </div>)}

            <div className="shadowPopup">
                <div className='popupBook'>
                    {showStepPopup()}
                </div>
            </div>
        </>
    );
}

export default PopupBook;
