import { useState } from "react";
import {
  createBrowserRouter,
} from "react-router-dom";
import Store from './pages/store';
import { BookingContext } from "./context/bookingContext";



const router = createBrowserRouter([
  {
    path: "/store/123",
    element: <Store />
  },
]);

function App() {


  const { servicesAuxSelected, setAuxServicesSelected } = useState([]);
  const { showAuxPopupBook, setAuxShowPopupBook } = useState(false);

  const setSelectService = (service) => {
    setAuxServicesSelected(services => [...services, service]);
  }

  const servicesSelected = () => {
    return servicesAuxSelected;
  }

  const setShowPopupBook = (showPopup) => {
    setAuxShowPopupBook(showPopup);
  }

  const showPopupBook = () => {
    return showAuxPopupBook;
  }


  return (
    <BookingContext.Provider value={{ showPopupBook, setShowPopupBook, servicesSelected, setSelectService }}>
      <div className="App">
        <header className="App-header">
          <Store />
        </header>
      </div>
    </BookingContext.Provider>
  );
}

export default App;
